-- Seleccionar registros ordenados por Columnas
SELECT campo_1,campo_2,campo_N FROM nombre_tabla ORDER BY campo_i ASC; 					-- Ordenar por nombre del campo en orden ascendente (Orden numérico u alfabético) 
SELECT campo_1,campo_2,campo_N FROM nombre_tabla ORDER BY campo_i DESC;					-- Ordenar por nombre del campo en orden descendente (Orden numérico u alfabético)